void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 pt_a = vec2(0.4,0.5);
    vec2 pt_b = vec2(0.5,0.4);

    vec2 nor = pt_a - pt_b;

    float temp = dot(uv-pt_b,normalize(nor));
    temp = smoothstep(0.0 ,length(nor),temp);

    vec3 col = vec3(temp);

    // Output to screen
    fragColor = vec4(col,1.0);
}
