#define cs vec2(250,130)
#define t(a) texture(iChannel0,ceil(((U+a)*cs/r))/cs)
#define s(a) step(a,U.x/r.x - sin(iTime))

void mainImage( out vec4 O, in vec2 U )
{
    vec2 r = iResolution.xy; vec2 uv = fract( (U/vec2(r.x, r.y)) * cs);
    float tr = t(vec2(5.5,0)).x,tg = t(vec2(0,5.5)).y, tb = t(vec2(-5.,-5.)).z;
    vec3 C = (U.x/r.x - sin(iTime)) < .5 ? vec3(tr,tg,tb) : vec3(1);
    O.rgb = C * vec3(1. - step(-.1 + .69 * tr, length(uv-vec2(.5)))) + (s(.499) - s(.501));
}
