void mainImage(out vec4 o, vec2 i) {
  vec2 maxRes = max(iResolution.xy, iResolution.yx);
vec2 minRes = min(iResolution.xy, iResolution.yx);
vec2 uv = maxRes == iResolution.xy ? i.xy / maxRes : i.yx / maxRes;


  // Add mouse input
  uv += iMouse.xy / iResolution.xy;

  // Create psychedelic effects
  uv *= 0.5;
  uv.x += sin(iTime * 2.0) * 0.2;
  uv.y += cos(iTime * 3.0) * 0.3;
  float r = length(uv);
  uv.x += sin(r * 10.0 + iTime) * 0.1;
  uv.y += cos(r * 20.0 + iTime * 1.5) * 0.1;

  // Add audio reactivity through iChannel1
  float audioLevel = texture(iChannel1, vec2(iTime * 0.1, 0.5)).r;
  uv += audioLevel * 0.1;

  o = tan(dot(uv, uv) * 666.6 + sin(iDate.wwxw));
}
