void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;

    uv -= .5;
    uv *= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec4 cir = vec4(smoothstep(.4, .1, length(uv)));

    vec2 iuv = floor(uv.xx*30.);
	vec4 dimg = vec4(hash12(iuv*314159.26));

    float wei = mouse.x;
    vec2 duv = vec2(uv.x, displace(uv, dimg.rg, .5, wei).y);

    vec2 ruv = vec2(radial(duv, vec2(0.), 5., iTime*.5));
    ruv += hash12(uv*314159.26);

    fragColor = vec4(smoothstep(1., .9, ruv.x))*cir.a;
}

