#define PI 3.1415926

float addition(float x) {
    // x: [0, 1)
    if (x < 0.1 && x > 0.) {
        // rand: [0, 2PI)
        float rand = x * 20. * PI;
        return (-cos(rand) + 1.) / 2.;
    } else {
        return 0.;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float d = iTime / 2. + (uv.y + uv.x) / 8. - 0.25;

    vec4 col = texture(iChannel0, uv) + addition(d);
    // vec4 col = mix(texture(iChannel0, uv), vec4(1.,0.,0.,1.), addition(d));
    // vec4 col = mix(texture(iChannel0, uv), texture(iChannel1, uv), addition(d));

    fragColor = vec4(col);
}
