void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame == 0) {
        fragColor = vec4(0.0);
    } else {
        vec2 uv = fragCoord/iResolution.xy;
        vec2 m1 = texture(iChannel0, uv).zw;
        fragColor = vec4(m1,iMouse.xy);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = fragCoord/iResolution.xy;

    vec4 bufA = texture(iChannel0, uv);
    vec2 mouse1 = bufA.xy;
    vec2 mouse2 = bufA.zw;
    vec2 vel = mouse2-mouse1;
    vec2 v_m1 = fragCoord.xy - mouse1;
    vec2 vel_n = normalize(vel);
    vec2 v_parallel = dot(vel_n, v_m1)*vel_n;
    vec2 v_perp = v_m1 - v_parallel;

    vec3 col = vec3(0.0,0.0,0.0);
    if(length(v_perp) < 20. && length(v_parallel) < 10.){
        col = vec3(1.0,0.0,0.0);
    }
    col = col + texture(iChannel1, uv).xyz;
    fragColor = vec4(col,0.0);
}
