    vec3 palette( in float t )
{
    vec3 a = vec3(0.728, 0.658, 0.658);
    vec3 b = vec3(0.228, 0.448, 0.548);
    vec3 c = vec3(0.108, 1.128, 1.0);
    vec3 d = vec3(-0.072, 0.468, 0.667);
    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord / iResolution.xy*2.0-1.0;
   uv.x*= iResolution.x/iResolution.y;
   vec2 uv0 = uv;
   vec3 finalColor = vec3(0.0);

   for(float i=0.0; i<2.0; i++) {
       uv = (fract(uv * 2.0) - 0.5) / exp(length(uv));


       float d = exp(length(uv)) * exp(-length(uv0));

       vec3 col = palette(length(uv0) + iTime);
       uv/= 30.0;

       d = sin(d*8.+ iTime)/16.;
       d = abs(d);
       d = 0.01/d;

       finalColor += col * d;
       }

   fragColor = vec4(finalColor, 1.0);
}
