// Thanks IQ for this one
// https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}


vec3 rgb2grayscale(vec3 rgb)
{
    // to test other posibility
    if(iMouse.z > 0.0) return vec3(0.22*rgb.r + 0.48*rgb.g + 0.114*rgb.b); // based by my sense

    return vec3(0.299*rgb.r + 0.587*rgb.g + 0.114*rgb.b); //https://www.mathworks.com/help/matlab/ref/rgb2gray.html
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    float hue = round((uv.x-1.0/12.0)*6.0)/6.0;
    float saturation = 1.0;
    float value = 1.0;

    vec3 c = vec3(hue, saturation, value);
 	vec3 rgb = hsv2rgb(c);

    if(uv.y < 0.33) {
        rgb = rgb2grayscale(hsv2rgb(c));
        rgb = pow(rgb, vec3(1.0/2.2));
    }

    if(uv.y > 0.66) {
        rgb = rgb2grayscale(hsv2rgb(c));
        rgb = pow(rgb, vec3(1.0/1.6)); //why it is looks better than 2.2?
    }

   	fragColor.rgb = rgb;
}
