void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float r1 = (sin(iTime-uv.y*3.0)+3.2)/10.0;
    float r2 = (sin(iTime-uv.y*3.0)+6.5)/10.0;
    float s1 = (sin(iTime-uv.y*3.0))/3.0;
    float s2 = (sin(iTime-(uv.y-0.32)*10.0))/3.0;

    if(r1 > uv.x){
                fragColor = vec4(1.0,0,.2,1)*(s1+0.45)/0.65;
        } else if(r2 > uv.x){
                fragColor = vec4(0,1,0.5,1)*(s1+0.45)/0.65;
        } else if(r2 < uv.x){
                fragColor = vec4(0,.0,1,1)*(s1+0.45)/0.65;
        }

}
