void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r= (sin(iTime - uv.y*1.0)+3.0)/5.0;
    float t= (sin(iTime - uv.y*1.0)+1.0)/5.0;

   if(r > uv.x)
       {
       fragColor = vec4(1, 0.9, 0, 1)*(r+5.0);
       }
       else 

         fragColor = vec4(0.7, 0, 0, 0)*(r+5.0);
       if(t > uv.x)

      {
        fragColor = vec4(0, 0, 0.8, 1)*(r+5.0);
       }


}
