#define lavacolor vec3(1,0.25,0)
#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

highp vec3 getlava(
	highp vec2 x){
highp vec3 col;
for(int i = 0; i<16; i++){
x *= 1.5;
x.x += iTime/64.0;
x *= rot(1.0);
x += (sin(x.x+x.y+x.x*2.0-iTime/4.0)+
	cos(x.x*4.0))/16.0;
col.r += sin(x.x*2.0)*cos(x.y+iTime);
col.g += cos(x.x+x.y-cos(x.x-x.y+iTime*
	float(i)-x.y+x.x*4.0));
col.b += cos(x.x+x.y+iTime+cos(x.x-x.y)+
	iTime);
	}
return col;
	}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / (
	iResolution.x, iResolution.y);

highp vec3 col = getlava(uv);

	fragColor = vec4(length(col/3.0)*lavacolor, 1.0);
}
