mat3 laplacian = mat3(
-1, -1, -1,
-1,  8, -1, 
-1, -1, -1
);

float edge(vec2 coord, int ind)
{
    float value = 0.0;
    float p;

    for(int i=-1; i<=1; i++) {
    for(int j=-1; j<=1; j++) {
        p = texelFetch(iChannel0, ivec2(coord)+ivec2(i, j), 0)[ind];
        p *= laplacian[i+1][j+1];

        value += p;
    }}  

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float v = edge(fragCoord, 0)+edge(fragCoord, 1);
    v /= 4.;

    fragColor = vec4(v, v, v, 1.0);

    if(abs(fragCoord.y-iResolution.y/2.)<=1. && fragCoord.x<iResolution.x/2.){
        fragColor = vec4(1., 0., 0., 1.);
    }
}

#define PI 3.1415926535897932384626433832795

vec2 complex(vec2 a, vec2 b)
{
    return mat2(a,-a.y,a.x)*b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec2 rt = vec2(0.5*log(dot(xy, xy)), atan(xy.y, xy.x));
    rt = 4.*rt/PI;

    float t = mod(iTime/5., 2.*PI);
    vec2 ab = vec2(cos(t), sin(t));

    vec2 uv = fract(complex(ab, rt));
    fragColor = vec4(uv, 0., 1.);
}
