void mainImage( out vec4 fragColor, in vec2 fragCoord )

{

 vec2 uv = fragCoord;

 uv -= vec2(0.2,0.6) * iResolution.xy;

 vec3 col = vec3(0,0.4,0.6);

 float r =100.0;
 if(uv.x * uv.x + uv.y * uv.y < r * r)
 {
  float z = sqrt(r * r - uv.x * uv.x - uv.y * uv.y);
  vec3 normal = normalize(vec3(uv.x,uv.y,z));
  vec3 light = normalize(vec3(1.0,1.0,1.0));  
  float diffuse = clamp(dot(normal, light),0.0,1.0);
  float I = diffuse * 0.8+0.6;

  vec3 d = vec3(0.0,0.0,-0.9);
  vec3 reflection = reflect(d,normal);

  col = vec3(0.5,0.4,0.9) * I +
        vec3(1.0,1.0,1.0)*pow(clamp(dot(reflection, light),0.6,0.9),6.0);
  }

  fragColor = vec4(col,1.0);


}
