// Based on https://thebookofshaders.com/13/
float random(in vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}
float noise(in vec2 st)
{
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;

}
float fbm ( in vec2 _st, int lp) {
    vec2 st = _st;
    float v = 0.f;
    float f = 1.f;
    float a = 0.5f;
    for(int i = 0; i != lp; i++)
    {
        mat2 R = mat2(cos(.5f), sin(.5f), -sin(.5f), cos(.5f));
        v += noise(st * f) * a;
        st = R * st * 2.f + 100.f;
        f *= 2.f;
        a *= 0.5f;
    }
    return v;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;    
    st.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(0.f);
    vec2 q = vec2(0.);
    q.x = fbm( st + 0.00*iTime,4);
    q.y = fbm( st + vec2(1.0),3);
    vec2 r = vec2(0.);
    r.x = fbm( st + 1.0*q + vec2(1.7,9.2)+ 0.15*iTime,3);
    r.y = fbm( st + 1.0*q + vec2(8.3,2.8)+ 0.126*iTime,4);
    float f = fbm(st+r,4);

    vec3 col_1 = vec3(20, 30, 10) / 255.f;
    vec3 col_2 = vec3(200, 230, 255) / 255.f;

    col = mix(col_1, col_2, f);

    fragColor = vec4(col, 1.f);
}
