#define PI          3.14159265358979323846264
#define TWO_PI      6.28318530717958647692528

#define scaleSize   5.0
#define swirlSpeed  -3.0
#define swirlScale  3.0
#define swirlBend   4.0
#define swirlBands  1.0
#define timeScale   0.05

vec3 tint_x = vec3(0.75, 0.5, 1.0);
vec3 tint_y = vec3(0.5, 0.75, 1.0);
vec2 center = vec2(0.5, 0.5);
vec2 zero_vec = vec2(0.0, 0.0);
vec2 right = vec2(1.0, 0.0);
vec2 left = vec2(-1.0, 0.0);

float speed = swirlSpeed;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * speed;
    float cosTime = 1.0 + abs(cos(time * timeScale));

    // Normalized pixel coordinates (from 0 to 1)
    float mn = min(iResolution.x, iResolution.y);
    vec2 res = iResolution.xy / mn;
    vec2 uv = fragCoord / mn;
    uv -= (center * res);
    uv *= (scaleSize * cosTime);

    // Calculate Polar Coordinates
    float r = distance(uv, zero_vec);
    float theta;
    if (uv.y < 0.0) {
      theta = acos(dot(normalize(uv), right));
    } else {
      theta = PI + acos(dot(normalize(uv), left));
    }   

    vec2 swirl_uv = vec2(r - (theta * swirlScale), theta - r * swirlBend);

    float swirl_level_x = (1.0 + cos((time + swirl_uv.x) * swirlScale)) * 0.5;
    swirl_level_x = round(swirl_level_x * swirlBands) / swirlBands;

    float swirl_level_y = (1.0 + cos((time + swirl_uv.y) * swirlBend)) * 0.5;
    swirl_level_y = round(swirl_level_y * swirlBands) / swirlBands;

    // Time varying pixel color
    vec3 col = clamp((swirl_level_y * tint_y) + (swirl_level_x * tint_x), 0.0, 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
