const vec3 C0 = vec3(1.0);
const vec3 C1 = vec3(0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy * 2. -1.;
    uv.x*=iResolution.x/iResolution.y;

    uv *= 1.0;

    // Time varying pixel color
    vec3 col;
    uint b = 1u;
    vec2 o0 = vec2(0.25, .50);
    vec2 o1 = vec2(-0.25, .0);

    b = step(fract(uv.x + uv.y), 0.5) * step(fract(uv.x - uv.y), 0.5) > 0.0 ? 1u : 0u;
    b = b ^ (fract(uv.x) <= 0.250 ? 1u : 0u);
    b = b ^ (fract(uv.x) <= 0.750 ? 1u : 0u);
    b = b ^ (fract(uv.x) <= 0.50 ? 1u : 0u);

    b = b ^ (distance(fract(uv-o0), vec2(1.)) >= 0.25 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o0), vec2(0.)) >= 0.25 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o0), vec2(1.,0.)) >= 0.25 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o0), vec2(0.,1.)) >= 0.25 ? 1u : 0u);

    b = b ^ (distance(fract(uv-o0), vec2(1.)) >= 0.125 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o0), vec2(0.)) >= 0.125 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o0), vec2(1.,0.)) >= 0.125 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o0), vec2(0.,1.)) >= 0.125 ? 1u : 0u);


    b = b ^ (distance(fract(uv-o1), vec2(1.)) >= 0.25 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o1), vec2(0.)) >= 0.25 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o1), vec2(1.,0.)) >= 0.25 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o1), vec2(0.,1.)) >= 0.25 ? 1u : 0u);

    b = b ^ (distance(fract(uv-o1), vec2(1.)) >= 0.125 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o1), vec2(0.)) >= 0.125 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o1), vec2(1.,0.)) >= 0.125 ? 1u : 0u);
    b = b ^ (distance(fract(uv-o1), vec2(0.,1.)) >= 0.125 ? 1u : 0u);


    col = b == 1u ? C0 : C1;

    // Output to screen
    fragColor = vec4(col,1.0);
}
