vec3 palette( in float t )
{
    vec3 a = vec3(0.538, 0.538, 0.318);
    vec3 b = vec3(0.352, 0.358, 0.538);
    vec3 c = vec3(0.848, 0.718, 0.988);
    vec3 d = vec3(.042, 0.442, 0.667);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
  uv.x *= iResolution.x/iResolution.y;
  float d = length(uv.y);
  vec3 col = palette(d+iTime*.25);
  d = sin(d*5.0 -iTime/2.0)/10.0;
  d = abs(d);
  d = 0.02/d;
  col *= d;
  fragColor = vec4(col,1.0);
}
