void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    uv -= vec2(0.5, 0.5) * iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float r=300.0;
    if(uv.x*uv.x+uv.y*uv.y<r*r){
        float z=sqrt(r*r-uv.x*uv.x-uv.y*uv.y);
        vec3 normal=normalize(vec3(uv.x,uv.y,z));
        vec3 light=normalize(vec3(1.0,0.0,0.0));
        float diffuse=clamp(dot(normal,light),0.0,1.0);
        float I=diffuse*0.5+0.4;

        vec3 d=vec3(0.0,0.0,-1.0);
        vec3 reflection=reflect(d,normal);


        col=vec3(1,0,0) * I+
            vec3(0.5,1.0,0.5)*pow(clamp(dot(reflection, light),0.0,1.0),5.0);
     }

    // Output to screen
    fragColor = vec4(col,1.0);
}
