void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);
    float size = 0.3; 
    float speed = 1000.0;
    vec2 ball = vec2(cos(iTime * speed), sin(iTime * speed)) * size;
    vec2 rotatedUV = uv - ball;
    float color = smoothstep(size - 0.02, size + 0.02, length(rotatedUV));
    fragColor = vec4(vec3(0.95- color), 1.0);
}
