#ifdef GL_ES
precision mediump float;
#endif

float rand2(vec2 st){
    vec2 f = mod(st,7.046);
    return fract(sin(f.x*328.575+f.y*421.784)*1246.547);
}

float value_noise(vec2 st){
    vec2 i=floor(st);
    vec2 j=smoothstep(0.,1.,fract(st));
    return mix(mix(rand2(i),rand2(i+vec2(1.,0.)),j.x),
               mix(rand2(i+vec2(0.,1.)),rand2(i+vec2(1.)),j.x),j.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    float vn=value_noise(st*10.);
    fragColor = vec4(vec3(vn),1.0);
}
