// Example of how to do ping-pong sampling in GLSL
// See https://thndl.com/triangle-waves.html

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 screenCoord = fragCoord/iResolution.xy*2.;
    vec2 pingPong = abs(2.*fract(screenCoord) - 1.);


    // Output to screen
    fragColor = texture(iChannel0, pingPong);
}
