void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int it_max = 300;
    float r_max = 2.0;

    float box_size = 1.0 / (pow(iTime,2.0));
    float center_x = -1.78;
    float center_y = 0.0;

    vec2 uv = fragCoord/iResolution.xy;
    float z0_re = center_x - box_size / 2.0 + uv.x * box_size;
    float z0_im = center_y - box_size / 2.0 + uv.y * box_size;

    float z_re = 0.0;
    float z_im = 0.0;
    float z_abs = 0.0;
    int it = 0;
    while (z_abs <= r_max) {
        if (it == it_max) {
            it = 0;
            break;
        }
        it = it + 1;

        float n_z_re = z_re*z_re - z_im*z_im + z0_re;
        z_im = 2.0*z_re*z_im + z0_im;
        z_re = n_z_re;
        z_abs = sqrt(z_im * z_im + z_re * z_re);
    }

    float g = float(it) / float(it_max);
    fragColor = vec4(g, g, g, 1.0);
}
