void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= 1.777777777;
    uv.y = 1.0 - uv.y;

    uv -= 0.5;
    uv *= 2.0;
    vec3 color = vec3(abs(uv), 0.0);

    if (fragCoord.x > 450.0) color = vec3(0.0);
    fragColor = vec4(color, 1.0);
}
