float N21(vec2 v) {
	float noise = dot(v, vec2(123.4, 234.5));
	noise = sin(noise) * 675445.2;
	noise = fract(noise);
	return noise;
}

mat2 rotate(float angle) {
	float c = cos(angle);
	float s = sin(angle);
	return mat2(
		 c, s,
		-s, c
	);
}

float smoothnoise(vec2 v, float scale) {
	v *= scale;
	vec2 id = floor(v);
	vec2 frac = fract(v);
	frac = smoothstep(0.0, 1.0, frac);

	float n00 = N21(id);
	float n10 = N21(id + vec2(1.0, 0.0));
    float n01 = N21(id + vec2(0.0, 1.0));
    float n11 = N21(id + vec2(1.0, 1.0));

	float b = mix(n00, n10, frac.x);
    float t = mix(n01, n11, frac.x);
	float val = mix(b, t, frac.y);

	return val;
}

float octaveNoise(vec2 v) {
	float n = smoothnoise(v * rotate(iTime), 4.0);
	n += smoothnoise(v * rotate(iTime * 0.5), 8.0) * 0.5;
    n += smoothnoise(v * rotate(iTime * 0.25), 16.0) * 0.25;
    n += smoothnoise(v * rotate(iTime * 0.125), 32.0) * 0.125;
    n += smoothnoise(v * rotate(iTime * 0.0625), 64.0) * 0.0625;
	n /= 1.9325;

	return n;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

	vec3 col = vec3(octaveNoise(uv), octaveNoise(uv + vec2(0.05, 0.0) * uv), octaveNoise(uv + vec2(0.0, 0.05) * uv));
	fragColor = vec4(col, 1.0);
}
