void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx;
    vec3 col = texture(iChannel0,uv).rgb*0.2;
    float time1 = sin(iTime * .1) * 10.;
    float time2 = cos(iTime * .1) * 10.;

    col += texture(iChannel0,uv+ 
     vec2(time1 *.05,0.) +0.5 * texture(iChannel0,uv+ 
     vec2(0.,time2 *.025) + 0.25 *texture(iChannel0,0.5*uv+
     vec2(time1 *.0125,-time2 *.0125) + 0.125 *texture(iChannel0,.25*uv).rg).rb).gb).rgb;


    fragColor = vec4(col * col * col * 4.,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float r = textureLod( iChannel0, (fragCoord) / iResolution.xy, 0.0 ).r;
float g = textureLod( iChannel0, (fragCoord +64.) / iResolution.xy, 0.0 ).r;
float b = textureLod( iChannel0, (fragCoord + 256.) / iResolution.xy, 0.0 ).r;
fragColor = vec4(r,g,b,1);
}
