void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
	vec2 uv = st;

    vec3 color = vec3(0.);

    uv = floor(uv*100.);
    vec2 ut = uv;

    float t = mod(floor(iTime*20.), 100.)-70.;
    // uv.x = mod(uv.y, uv.x*abs(sin(uv.y)));
    uv.x += t/uv.y;
    // uv.x = mix(mod(uv.x*uv.y, 4.), mod(uv.x*uv.y, 3.), .1);
    // uv.y += mix(mod(uv.x*uv.y, 5.), mod(uv.x*uv.y, 3.), .9);
    // uv.x -= mix(mod(uv.x*uv.y, 44.), mod(uv.x*uv.y, 55.), .1);

	float r = mod(uv.x*uv.y, 100.)/110.;
    float cc = mod(1.-distance(ut, vec2(50.)), r*2.);
    // r /= 2.;
    float a = mod(1.-distance(ut, vec2(25.)), 2.+r);
    float b = mod(1.-distance(ut, vec2(75.)), 2.+r);
    float c = mod(1.-distance(ut, vec2(25., 75.)), 2.+r);
    float d = mod(1.-distance(ut, vec2(75., 25.)), 2.-r);

    float j = 0.;
    float k = 0.;
    float acc = fract(d+uv.x-uv.y*c*b/a);
    for (int i = 0; i < 25; i++) {
        j = mod(j+float(i)*ut.y, cc);
        k = mod(j+float(i+1)*ut.x, cc);
        acc = mix(acc, mod(1.-distance(uv, vec2(j, k)), 2.+t/10.), .9);
    }
    r = abs(acc);

    acc = abs(fract(acc));
    r = mix(mix(a, d, acc), mix(b, c, 1.-acc), .5)/5.;
    // r = mod(r, cc);
    r = clamp(.2, .5, r);
    color = vec3(r);
    if (r > .8) {
        color *= vec3(0., 1., 0.);
    }
    if (r > .5 && r < .7) {
        color *= vec3(0., 1., 1.);
    }
    if (r > .2 && r < .51) {
        color *= vec3(1., .5, .5);
    }
    if (r < .21) {
        color *= vec3(1., .1, .1);
    }

    fragColor = vec4(color,1.0);
}
