vec4 getChannel0(vec2 pos) {
    return texture(iChannel0, pos);
}

vec4 getChannel1(vec2 pos) {
    return texture(iChannel1, pos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 WebcamInput = getChannel0(uv);
    vec4 image = WebcamInput;
    vec3 inverseImage = vec3(1.0, 1.0, 1.0) - getChannel1(uv).xyz;
    fragColor = image;
    fragColor.xyz += inverseImage;
    fragColor = fragColor;

    vec3 col = ((0.5 + 0.5*cos(2.0*iTime+uv.xyx+vec3(0,2,4)))*4.0);
    fragColor.xyz = vec3(1.0, 1.0, 1.0) - fragColor.xyz;
    fragColor.xyz = fragColor.xyz*sqrt(col);
    fragColor = abs(fragColor)*2.0;
    fragColor.xyz = pow(fragColor.xyz, vec3(2.0, 2.0, 2.0));

    //Comment me out for just the outline
    //fragColor += image;

    //And uncomment me for a grey screen
    fragColor += 0.5;
}
