const float M_PI = 3.1415926535897932384626433832795;
const float speed = 1.0;
const float size = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 hres = iResolution.xy/2.0;

    vec2 center = vec2(hres.x, hres.y*cos(iTime*speed))+vec2(0.0, hres.y);
    vec3 col;

    if(length(fragCoord.xy-center.xy) < size) {
        col = vec3(0.5+0.5*cos(iTime*speed*2.0));
    } else {
        col = vec3(1.0);
    }


    center = vec2(hres.x*cos(iTime*speed), hres.y)+vec2(hres.x, 0.0);
    if(length(fragCoord.xy-center.xy) < size) {
        col = vec3(0.5+0.5*cos(iTime*speed*2.0));
    }


    fragColor = vec4(col,1.0);
}
