void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;
    uv.x= uv.x * (iResolution.x/iResolution.y);
    uv=uv-0.5;
    uv.x=uv.x-0.35;
    uv=uv*10.; //zoom out

    float distance = length(uv);
    distance = sin(distance + iTime);
    distance = abs(distance);
    distance = sin(iTime)/distance;

    float redCol=0.1/40.*sin(cos(10.*distance)*sin(0.01*iTime));


    fragColor = vec4(cos(iTime*redCol),1.0-distance*sin(iTime*redCol),sin(distance+iTime),1.0);
}
