void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv.y += 1.0;

    float gd = length(uv);
    vec3 finCol = vec3(0.0);
    vec3 col = 2.0 * vec3(0.5 + sin(iTime), 0.5 + sin(iTime + 3.1415), gd);

    float speed = 0.03 * 3.1415;

    for (float i = 1.0; i < 18.0; i++) {
        float r = i / 10.0;

        float d = length(uv.xy - vec2(r * cos(iTime * i * speed), r * abs(sin(iTime * i * speed))));
        d -= 0.01;
        d = smoothstep(0.0, 0.2, d);
        d = 0.001 / d;

        finCol += d * col;
    }

    fragColor = vec4(finCol, 1.0);
}
