void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    vec4 texColor = texture(iChannel0,xy);

    float luminance = (texColor.r + texColor.g + texColor.b) / 3.0;

    vec3 grayscaleColor = vec3(luminance);

    vec3 originalColor = texColor.rgb;

    float t = (sin(iTime*2.0) + 1.0) * 0.5;

    vec3 mixedColor = mix(grayscaleColor, originalColor, t);

    texColor.rgb = mixedColor;

    fragColor = texColor;
}
