/* Minimal Line thingee. Great for experimenting etc.
Hope your better at this than me ;), g'luck chummers! */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -.5 * iResolution.xy)/iResolution.y;

    float d = smoothstep(.001,.0009, abs(uv.x - uv.y));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,1,3));

    // Output to screen
    fragColor = vec4(col*d,1.0);
}
