void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;


    uv -= 0.5; // 往左下移动画布，等效于被绘制物体往右上角移动(+0.5)
    uv.x *= iResolution.x / iResolution.y;

    float d = length(uv);
    float r = 0.1;
    float g = 0.02;

    // if(d < 0.5) c = 1.0; else c = 0.0;
    float c = smoothstep(r, r - g, d);


    vec3 col = vec3 (c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
