void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = (fragCoord.x / iResolution.x) * 2.0 - 1.0;
    float y = (fragCoord.y / iResolution.y) * 2.0 - 1.0;
    float t = (iTime * 3.) - 33.333;
    float gray = cos(sin (x*5.-t) + cos (y*3.+t) * sin(x*y*2.+t) + sin (y*7.*t) - sin(x*9.*t));
    gray = gray * 0.5 + 0.5;
    fragColor = vec4(gray, gray, gray, 1.0);
}
