void mainImage( out vec4 O, vec2 f ) {
  vec3 v=texelFetch(iChannel0,ivec2(f),0).rgb;
  O.rgb=min(12.9*v,abs(1.054*pow(v,v-v+.4166)-.095)+.04);// https://www.shadertoy.com/view/sl3cRs
}

uint triple32(uint x)// https://www.shadertoy.com/view/WttXWX
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

void mainImage( out vec4 v, in vec2 f ) {
  vec2 R=iResolution.xy;
  vec2 u=f/R;
  // u=.5+(-.5+u)*exp(-.1*iTimeDelta);
  v=texture(iChannel0,u);
  uvec4 s;
  for(int i=0;i<4;i++)s[i]=triple32(uint(iFrame*4+i));
  vec4 p=vec4(s)/vec4(~0u);
  p.zw-=p.xy;
  float g=clamp(dot(u-p.xy,p.zw)/dot(p.zw,p.zw),0.,1.);
  // A modification idea to not flash the image so much
  // g=clamp(1.-4.*abs(g-.5),0.,1.);
  if(iFrame<98)
    v=abs(g-v);
}
