float sdfRect(in vec2 p, in vec2 b)
{
    vec2 d = abs(p) - b;
    return max(d.x, d.y);
}

float opOnion(in float d, in float r)
{
    return abs(d) - r;
}

vec2 rotate(vec2 p, float a) {
	float c = cos(a);
    float s = sin(a);
    mat2 m = mat2(c, s, -s, c);
    return m * p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 point = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 mouse = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;

    vec2 center = mouse;
    vec2 size = vec2(0.4, 0.7);
    float border = 0.05;

    float dist = sdfRect(rotate(point - center, iTime), size);
    dist = opOnion(dist, border);
    dist = opOnion(dist, border / 4.0);

    float value = smoothstep(0.0, 0.0, dist);
    fragColor = vec4(vec3(value), 1.0);
}
