vec3 palette( in float t)
{
    vec3 a = vec3(0.55,0.55,0.55);
    vec3 b = vec3(0.57,0.57,0.58);
    vec3 c = vec3(0.9,0.9,0.9);
    vec3 d = vec3(-0.68,-1.44,-0.22);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float gd = length(uv);
    vec3 finCol = vec3(0.0);




    for (float i = 0.0; i < 5.0; i++) {

        float speed = 0.1* i;
        float amp = 1.0 / (uv.x * uv.x + 1.);
        float freq = 6.0;

        float d = amp * sin((uv.x + iTime * speed) * freq) - uv.y;
              d /= cos((uv.x + iTime * speed) * freq)-uv.y;
        vec3 col = palette(d+iTime+i*4.);
        d = abs(d);

        d = pow(0.02/d,1.2);


        finCol += d * col;
    }

    fragColor = vec4(finCol, 1.0);
}
