#define M_PI 3.1415926535897932384626433832795


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-1.0-iResolution.xy)/iResolution.y;
    float d=length(uv);
    d=sin(d*(8.0+sin(iTime))+iTime)/(8.0+sin(iTime*8.0));
    d=abs(d);
    d=smoothstep(0.0,0.1,d);
    float angle=abs(atan(uv.y/uv.x));
    float pseudo=sin(angle*8.0*sin(iTime));
    fragColor = vec4(pseudo,pseudo,d,1.0);
}
