vec2 uver(vec2 fragCoord, vec3 iResolution, sampler2D image){
    vec2 uv = (fragCoord/iResolution.xy)-vec2(0.5,0.0);
    uv *= vec2(2.0,1.0);
    vec3 offs = texture(image, uv, 1.0).rgb;
    return (uv *( vec2((uv.y*20.0)+(1.0-uv.y)*4.0, 10.0))/5.0)+offs.xy;
}

vec3 colrotate(sampler2D img, vec2 uv, float iTime, float rotfactor){
    vec3 col = texture(img, uv+vec2(0.0,iTime/30.0),1.0).rgb;
    vec3 colout = vec3(0.7,0.5,0.7)-col;
    return mix(col.rrg, colout, rotfactor)*2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 maxcol = colrotate(iChannel0, uver(fragCoord, iResolution, iChannel0), iTime, 1.0)-0.8;
    float height = maxcol.g;
    int itercount = 30-int((height)*30.0);
    int iter = itercount;
    for (int i=0; i<iter; i++){
        vec3 currcol = colrotate(iChannel0, uver(fragCoord+vec2(0.0,i), iResolution, iChannel0), iTime, 1.0);
        bool higher = currcol.g<height;
        if(higher){
            maxcol = currcol;
            height = currcol.r;
        }
    }
    vec3 col = maxcol;

    // Output to screen
    fragColor = vec4(col,1.0);
}
