void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 center = vec2(0.2);


    float distance = length(uv - center);


    float zoomSpeed = 0.2;
    float zoomAmount = 5.0;
    float zoom = pow(1.0 - distance, zoomAmount) * zoomSpeed * iTime;


    uv = uv * (1.0 - zoom) + center * zoom;


    vec2 matrixCoord = fract(uv * 15.0);
    float matrix = step(0.5, matrixCoord.x) * step(0.4, matrixCoord.y);

    vec3 color = vec3(matrix);

    fragColor = vec4(color, 1.0);
}
