float apertureSize = 0.05;  // Adjust the size of the aperture
float focalDepth = 0.5;     // Adjust the focal depth

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;

    float depth = uv.y;  
    float depthDifference = abs(depth - focalDepth);

    float blurAmount = step(apertureSize, apertureSize + 0.1, depthDifference);

    // Box Blur
    vec3 blurredColor = vec3(0.0);
    for (int i = -5; i <= 5; i++) {
        blurredColor += texture(iChannel0, uv + vec2(float(i) / iResolution.x, 0.0)).rgb;
    }
    blurredColor /= 11.0;

    // Mix the blurred color with the original color based on the bokeh filter
    color = mix(color, blurredColor, blurAmount);
    fragColor = vec4(color, 1.0);
}
