#define R iResolution.xy
#define PI 3.1415926
#define rev3 0.33333333
#define W (3./R.y)

float grids(vec2 uv,vec2 g1,vec2 g2) {
    return step(g1.x,uv.x)*(1.-step(g2.x,uv.x))*step(g1.y,uv.y)*(1.-step(g2.y,uv.y));
}

void mainImage( out vec4 c, in vec2 uv )
{
    // not very smooth
    float t=1.-pow(abs(fract(iTime*.2)-1.),.9);
    uv*=(t*2.+1.)/R.y;
    for (int i=0;i<7;i++) {
        c+=grids(mod(uv,3.),vec2(1.,1.),vec2(2.,2.));
        c+=grids(uv=fract(uv),vec2(rev3,rev3*2.),vec2(rev3,rev3*2.));
        uv*=3.;
    }
    c=1.-c;
}
