#define S(a, b, t) smoothstep(a, b, t)
#define offset 0.0002

    // Prepared shape Band by x,y / Rectangle by Top/Left, Bottom/Left / Margin by Top/Left
float bandX(float left, float width, vec2 st) {
  return S(left-offset, left, st.x) - S(left + width- offset, left + width, st.x);
}

float bandY(float bottom, float width, vec2 st) {
  return S(bottom - offset, bottom, st.y) - S(bottom + width- offset, bottom + width, st.y); 
}

float rectB(float bottom, float left, float width, vec2 st) {
  float ver = S(bottom - offset, bottom, st.y) - S(bottom + width- offset, bottom + width, st.y);
  float hor = S(left - offset, left, st.x) - S(left + width- offset, left + width, st.x);
  return hor * ver; 
}

float rectT(float top, float left, float width, vec2 st) {
  float hor = S(left-offset, left, st.x) - S(left + width- offset, left + width, st.x);
  float ver = S(top - offset, top, st.y) - S(top + width- offset, top + width, st.y);
  return hor * ver; 
}

float border(float top, float left, float width, vec2 st) {
  float hor = S(left-offset, left, st.x) - S(left + width- offset, left + width, st.x);
  float ver = S(top - offset, top, st.y) - S(top + width- offset, top + width, st.y);
  return hor * ver; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
  vec2 st = fragCoord/iResolution.xy;
    // Changed Aspect ratio
  float AR = iResolution.y/  iResolution.x;
  st.x *= 1. / AR;
    // Centered
  st.x += (1. - 1. / AR) / 2.;

    // Prepared shape
  float 
  line = bandX(.20, .03, st) 
  + bandY(.20, .03, st) 
  + bandY(.65, .075, st)
  + rectB(.05, .92, .085, st)
  + bandX(.89, .03, st);


    // Using shape for masking
  float b = rectT(0., 0., .2, st);
  float r = rectT(0.23, 0.23, 1., st);
  float y = rectT(0.,   0.92, .1, st);

    // Colors palette
  vec3 bg = vec3(0.85, 0.89, 0.91);
  vec3 blue = vec3(0.00, 0.32, 0.72);
  vec3 red = vec3(0.89, 0.17, 0.20);
  vec3 yellow = vec3(0.95, 0.87, 0.44);
  vec3 white = vec3(1., 1., 1.);

    //Mixing the potion
  float colorMask = b+r+y;

  vec3 br = mix( blue,red, r);
  vec3 bry = mix( br,yellow, y);

  vec3 canvas = mix( bg - line,bry, colorMask);

  vec3 color = mix(white, canvas,border(0., 0., 1., st));


  fragColor = vec4(color, 1.0);
}
