vec3 hsv(float h, float s, float v) {
    h=mod(h,360.0);
    vec3 rpgpbp;
    float c=v*s;
    float x=c*(1.0-abs(mod(h/60.0,2.0)-1.0));
    float m=v-c;
    if (h<60.0) {
        rpgpbp=vec3 (c,x,0.0);
    } else if (h<120.0) {
        rpgpbp=vec3 (x,c,0.0);
    } else if (h<180.0) {
        rpgpbp=vec3 (0.0,c,x);
    } else if (h<240.0) {
        rpgpbp=vec3 (0.0,x,c);
    } else if (h<300.0) {
        rpgpbp=vec3 (x,0.0,c);
    } else {
        rpgpbp=vec3 (c,0.0,x);
    }
    return vec3 (rpgpbp.r+m,rpgpbp.g+m,rpgpbp.b+m);
}

vec3 math(vec2 c, int iterations, vec2 constant) {
    float x=c.x;
    float y=c.y;
    float x2 = x*x;
    float y2 = y*y;
    int i=0;
    while (x2+y2<=4.0&&i<iterations) {
        y=abs(2.0*x*y) + constant.y;
        x=x2-y2 +constant.x;
        x2=x*x;
        y2=y*y;
        i++;
    }
    if (i<iterations) {
        return vec3 (hsv((float(i)+1.0-log2(log2(sqrt(x2+y2))/log2(10.0))),0.7,1.0));
    } else {
        return vec3 (0.0,0.0,0.0);
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 mousepos = (iMouse.xy-iResolution.xy*vec2(0.5,0.5))*(vec2 (2.25*iResolution.x/iResolution.y,-2.25)/iResolution.xy);
    vec2 creal= vec2 (cos(mousepos.x),sin(mousepos.y)); //change this c value to change the graph
    vec2 gspace = (fragCoord.xy-iResolution.xy*vec2(0.5,0.5))*(vec2 (2.25*iResolution.x/iResolution.y,-2.25)/iResolution.xy);
    int iterations = 500;


    vec3 col = math(gspace.xy, iterations, creal);

    fragColor = vec4(col,1.0);
}
