void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float move_time= 13.75*sin(iTime*.750);   
    float len=length(uv);
    float sin_dist=(sin((len)*3.1415*6.+(move_time)));
    vec4 col =vec4(.5,sin_dist*1.1,.5,1.0);  
    if (sin_dist < 0.){
        col =vec4(0.,len*.5,len*.5,1.0);
    }

    // Output to screen
    fragColor = col;
}
