void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int it_max = 20;
    float r_max = 2.0;
    float box_size = 3.0;
    vec2 center = vec2(-1.1, 0.0);

    vec2 c = vec2(cos(iTime), sin(iTime));
    vec2 uv = fragCoord/iResolution.y;

    vec2 z = center - box_size / 2.0 + uv * box_size;
    int it = 0;
    while (length(z) <= r_max) {
        if (it == it_max) {
            it = 0;
            break;
        }
        it = it + 1;

        z = vec2(z.x*z.x - z.y*z.y + c.x, 2.0*z.x*z.y + c.y);
    }

    float g = float(it) / float(it_max);
    fragColor = vec4(g, g, g, 1.0);
}
