// light2d_7_beer-lambert https://www.shadertoy.com/view/dl2yWG

#define TWO_PI 6.28318530718f
#define N 64
#define MAX_STEP 10
#define MAX_DISTANCE 2.0f
#define EPSILON 1e-6f

float random (vec2 pos) 
{
    return fract(sin(dot(pos.xy,vec2(12.9898,78.233)))*43758.5453123);
}

float circleSDF(vec2 pos,vec2 center,float r)
{
    return length(pos-center)-r;
}

float trace(vec2 origin,vec2 dir)
{
    float t = 0.0f;
    for(int i=0;i<MAX_STEP&&t<MAX_DISTANCE;i++)
    {
        float sd = circleSDF(origin+dir*t,vec2(0.0f,0.0f),0.2f);
        if(sd<EPSILON)
            return 2.0f;
        t += sd;
    }
    return 0.0f;
}

// https://stackoverflow.com/questions/33270823/how-to-cast-int-to-float-in-glsl-webgl
float lightSample(vec2 pos)
{
    float sum = 0.0f;
    float n_float = float(N);
    float rand = random(pos)+iTime;
    for(int i=0;i<N;i++)
    {
        float i_float = float(i);
        //float rand = random(pos+random(vec2(i_float,i_float)));
        //float a = TWO_PI*rand;// uniform sampling
        //float a = TWO_PI*i_float/n_float; // stratified sampling
        float a = TWO_PI*(i_float+rand)/n_float; // jittered sampling
        sum += trace(pos,vec2(cos(a),sin(a)));
    }
    return sum/n_float;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float col = lightSample(uv);
    vec4 data = texture(iChannel0,fragCoord/iResolution.xy);
    if(iFrame==0)
    {
        data=vec4(0.);
    }
    data += vec4(vec3(col),1.0);
    fragColor = data;
}
