vec2 g2n(vec2 uv) {
    float e = 0.001;
    float s0 = texture(iChannel1, uv).r;
    float sx = texture(iChannel1, uv + vec2(e, 0.0)).r;
    float sy = texture(iChannel1, uv + vec2(0.0, e)).r;
    return vec2(sx - s0, sy - s0) * 100.0;
}

void mainImage(out vec4 f, in vec2 c) {
    float i = 1.0;  
    float t = 0.1;  

    vec2 u = c.xy / iResolution.xy;
    vec2 d = (u - 0.5) * i;  

    float st = (iTime + texture(iChannel0, u).r) * t;
    float ft0 = fract(st);  
    float ft1 = fract(st + 0.5);  
    float a = abs((ft0 - 0.5) * 2.0); 

    vec4 s0 = texture(iChannel1, u + d * ft0);  
    vec4 s1 = texture(iChannel1, u + d * ft1);  

    f = mix(s0, s1, a);
}
