void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy)*2.0 - 1.0;

    //uv = fract(uv*1.0 + iTime)/1.2;
    float aspect_ratio = iResolution.x/iResolution.y;
    uv.x *= aspect_ratio;
    float col2;
    float num;
    float col3;
    // Time varying pixel color
    float col = 6.5*atan(iTime+uv.y-uv.x);
    float d = length(uv);

    for (float i; i<1.0;i++) {
        num+=iTime;
        col2 =  smoothstep(iTime/6.0, num, d);
        col3 =  tan(smoothstep(iTime/2.0, num-iTime, d));  
    }






    // Output to screen
    fragColor = vec4(col3+0.9, col2+0.5, col+0.1,1.0);

}
