#define pi 3.14159265359
#define tau (2.0*pi)
#define color(offset) abs(sin(offset + vec3(3, 7, 11) * pi/6.0))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 5.0*(fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    vec2 fv = fract(uv) - 0.5;
    float id = mod(floor(uv.x) + floor(uv.y), 2.0);
    vec2 idv = floor(abs(uv) + 0.5);

    float angle1 = iTime;
    float angle2 = -iTime;

    if(id == 0.0) fv *= mat2(cos(angle1), sin(angle1), -sin(angle1), cos(angle1));
    if(id == 1.0) fv *= mat2(cos(angle2), sin(angle2), -sin(angle2), cos(angle2));

    col += 0.0075/abs(max(abs(fv.y) - 0.125, 0.0) + 0.75*length(fv.x));
    col += 0.01/abs(fv.y + 0.125 + 0.75*length(fv.x));
    col += 0.01/abs(-fv.y + 0.125 + 0.75*length(fv.x));

    col *= 2.0*color(length(uv) + 2.0*iTime);

    fragColor = vec4(col,1.0);
}
